tql.tql:
SELECT * FROM smartsheet.add_rows
  WHERE sheetId=@sheetid
  AND allowPartialSuccess=true
  AND $body=(SELECT {
    'cells' : [ {
      'columnId' : @column2,
      'value' : @description,
    },
    {
      'columnId' : @column3,
      'value' : 'Received',
    },
    {
      'columnId' : @column4,
      'value' : @priority,
    },
    {
      'columnId' : @column5,
      'value' : 'Empty',
    },
    {
      'columnId' : @column6,
      'value' : @team,
    },
    {
      'columnId' : @column7,
      'value' : @department,
    },
    {
      'columnId' : @column11,
      'value' : @user,
    },
    {
      'columnId' : @column15,
      'value' : @date,
    },
    ]
  })

SELECT * FROM slack.post_chat_message
  WHERE $body=(SELECT {
    'channel': @channel,
    'as_user': true,
    'text': 'Helpdesk ticket created!',
    'attachments': [
      {
        'title': @header,
        'fields': [
          {
            'title': 'Description',
            'value': @description,
          },
          {
            'title': 'Status',
            'value': 'Received',
            'short': true,
          },
          {
            'title': 'Priority',
            'value': @priority,
            'short': true,
          },
          {
            'title': 'Team',
            'value': @team,
            'short': true,
          },
          {
            'title': 'Department',
            'value': @department,
            'short': true,
          },
        ],
      },
    ]
  })
